    private void btnLogarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLogarActionPerformed
        Funcionario f;
        FuncionarioDAO fdao = new FuncionarioDAO();
        
        f = fdao.getFuncionarioLogin(txtLogin.getText());

        if(txtLogin.getText().equals(f.getLogin()) && 
                String.valueOf(Senha.getPassword()).equals(f.getSenha())) {
            if(f.isAtivado()) {
                Menu menu = new Menu(f);
                menu.pack();
                menu.setVisible(true);
                this.dispose();
            } else {
                JOptionPane.showMessageDialog(rootPane, "Acesso negado!");
            }
        } else {
            JOptionPane.showMessageDialog(rootPane, "Login e/ou senha est√° incorreto");
        }
        
        
    }//GEN-LAST:event_btnLogarActionPerformed

